	empRouter.HandleFunc("/api/v1/orgs/employees/activity/costs/edit/{id}", controllers.EditActivityCostByAdmin).Methods("PUT")
	router.PathPrefix("/api/v1/admin/org/employees/").Handler(negroni.New(
		negroni.HandlerFunc(common.Authorize),
		negroni.Wrap(empRouter),
	))
	return router
